package com.model;

import com.annotation.Column;
import com.annotation.ID;

public class Stu {
	
	@ID
	@Column(name="SID")
	private int sid;
	@Column(name="SNAME")
	private String sname;
	@Column(name="SCORE")
	private double score;
	@Column(name="TID")
	private int tid;
	
	public Stu() {
		// TODO Auto-generated constructor stub
	}
	
	
	
	public Stu(int sid, String sname, double score, int tid) {
		super();
		this.sid = sid;
		this.sname = sname;
		this.score = score;
		this.tid = tid;
	}



	public int getSid() {
		return sid;
	}
	public void setSid(int sid) {
		this.sid = sid;
	}
	public String getSname() {
		return sname;
	}
	public void setSname(String sname) {
		this.sname = sname;
	}
	public double getScore() {
		return score;
	}
	public void setScore(double score) {
		this.score = score;
	}
	public int getTid() {
		return tid;
	}
	public void setTid(int tid) {
		this.tid = tid;
	}
	
	
	@Override
	public String toString() {
		return "Stu [sid=" + sid + ", sname=" + sname + ", score=" + score + ", tid=" + tid + "]";
	}
	
}
